insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Uova', 'Un alimento altamente nutriente e versatile, l uovo è una fonte di proteine di alta qualità. È comunemente usato nella cucina per preparare piatti dolci e salati, come torte, frittate, e salse', 'uova.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Guanciale', 'Un tipo di pancetta italiana che viene preparata utilizzando il grasso e la carne della guancia del maiale. Ha un sapore intenso e aromatico ed è spesso utilizzato come ingrediente principale nella famosa pasta all amatriciana e in altre ricette della cucina italiana', 'guanciale.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Pecorino', 'Il Pecorino Romano è un formaggio italiano stagionato, a pasta dura e dal sapore robusto e salato. Viene grattugiato su piatti come la pasta cacio e pepe o la pasta alla carbonara per aggiungere gusto e complessità', 'pecorino.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Spaghetti', 'Un alimento base della cucina italiana, la pasta è costituita principalmente da farina di grano e acqua. Può essere accompagnata da una vasta gamma di salse e condimenti', 'spaghetti.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Vino Bianco', 'Un vino ottenuto dalla fermentazione di uve bianche. Viene spesso utilizzato nella cucina italiana per aggiungere profondità e complessità ai piatti. Il vino bianco è un ingrediente comune in salse, stufati e marinature, e può anche essere servito come accompagnamento ai pasti', 'vino_bianco.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Pomodori Pelati', 'Pomodori privati della buccia, che vengono immersi in acqua bollente per renderne più semplice la rimozione. Questi pomodori sono pronti per essere utilizzati nella preparazione di salse, sughi e altri piatti che richiedono pomodori senza buccia', 'pelati.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Peperoncino Fresco', 'Una varietà di peperoncino piccante, il peperoncino fresco aggiunge un tocco di calore e sapore ai piatti. Viene spesso utilizzato nella cucina italiana e in molte altre cucine del mondo per aggiungere un po di vivacità e pizzico a piatti come pasta, carne e salse', 'peperoncino.png');
insert into ingrediente (id, nome, descrizione, url_Image) values(nextval('ingrediente_seq'), 'Pepe Nero', 'Una spezia comunemente usata ottenuta dalla macinazione dei semi essiccati del pepe nero. Aggiunge un sapore pungente e leggermente piccante ai piatti e viene utilizzato sia durante la cottura che come condimento finale', 'Pepe-nero.png');
